import React, { useState } from 'react';
import Expenses from "./components/Expenses/Expenses";
import NewExpense from './components/NewExpense/NewExpense';

const DUMMY_EXPENSES = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: "e2", 
    title: "New TV", 
    amount: 799.49, 
    date: new Date(2021, 2, 12),
  },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

function App() {

  //States
  const [expenses, setExpenses] = useState(DUMMY_EXPENSES);
  const [chosenYear, setChosenYear] = useState("2020");

  //Handlers
  //58. added this handler to accept data from below
  const addExpenseHandler = (expense) => {
    setExpenses((prevExpenses) => {
      return [expense, ...prevExpenses];
    });
  };

  const filterChosenHandler = (filteredYear) => {
    setChosenYear(filteredYear);
    setExpenses((prevExpenses) => {
      return prevExpenses.filter( i => filteredYear.includes(i.date));
      });
    };

  //Return statement
  //58. added this handler to accept data from below
  return (
    <div>
      <NewExpense onAddExpense={addExpenseHandler}/>
      <Expenses onFilterChosen={filterChosenHandler} items={expenses}/>
    </div>
  );
}

export default App;
